import React, { Component } from 'react';
import './FilterBar.css'

class FilterBar extends Component {
  constructor(props) {
    super(props);

    this.state = {
      filter: 'all',
    }
  }

  handleOnChange = e => {
    this.setState({
      filter: e.target.value,
    });
  }

  render() {
    const { state: { filter }, handleOnChange } = this;

    return (
      <div className="row">
        <form className="col m6 offset-m3 space-evenly">
          <label>
            <input 
              className="with-gap" 
              name="filter" 
              value="all" 
              type="radio" 
              checked={filter === 'all'}
              onChange={handleOnChange} 
            />
            <span>All</span>
          </label>
          <label>
            <input 
              className="with-gap" 
              name="filter" 
              value="active" 
              type="radio" 
              checked={filter === 'active'} 
            />
            <span>Active</span>
          </label>
          <label>
            <input className="with-gap" name="filter" value="completed" type="radio" checked={filter === 'completed'} />
            <span>Completed</span>
          </label>
        </form>
      </div>
    );
  }
}

export default FilterBar;
